import style from './ToDo.module.css';
import { useRef, useState } from 'react';
import { http } from '../../hooks/http.hooks';

const ToDo = () => {
    const [data, ] = useState({
        create: {
            user_id: '',
            tasks: ''
        },
        update: {
            user_id: '',
            tasks: '',
            id: ''
        },
        delete: {
            user_id: '',
            id: ''
        },
    });
    const createRef = useRef('')
    const deleteRef = useRef('')
    const updateRef = useRef('')


    const sendCRUD = async () => {
        try {
            const data = await http('http://localhost:5000/users/auth', 'POST', {  })
            console.log(data);
        } catch (err) {
            console.log(err.message);
        }
    }

    return (
        <div className={style['wrapper']}>

            <p className={style['label']}>To do:</p>
            <div className={style['block-flex-column']}>

                <div className={style['block-flex-column']}>
                    <p>Create:</p>
                    <div>
                        <input ref={createRef} placeholder='task' />
                    </div>
                </div>

                <div className={style['block-flex-column']}>
                    <p>Update:</p>
                    <div className={style['flex']}>
                        <div>
                            <input ref={updateRef} placeholder='Number' />
                        </div>
                        <div>
                            <input placeholder='task' />
                        </div>
                    </div>
                </div>

                <div className={style['block-flex-column']}>
                    <p>Delete:</p>
                    <div>
                        <input ref={deleteRef} placeholder='Number' />
                    </div>
                </div>

                <div className={style['btn']}>+ save</div>

            </div>
        </div>
    );
}

export default ToDo;
