const http = async (url, method, body, header) => {
    try {
        if (body) {
            body = JSON.stringify(body);
            header['Content'] = 'application/json';
        }

        const response = await fetch(url, { method: method, header: header, body: body });
        const parsedData = await response.json();
        return parsedData;

    } catch (error) {
        console.log(error.message);
    }
}
export { http };
